import * as React from 'react'
import book from '../../assets/livro01.webp';
import api from '../../services/api'

export default function MocksTeste () {

    const [books, setBooks] = React.useState([])

    React.useEffect(() => {
        const getBooks = async () => {
            const response = await api.get('/books')
            setBooks(response.data)
            console.log(response.data)
        }

        getBooks()
    },[])
    return (
        <div className="card">
                {
                    books.map(book => (
                        <div className="card__body">
                            <img className='img__book' src={book} alt='Livro' />
                            <h5 className="card__title">{book.title}</h5>
                            <h4 className="card__subtitle"></h4><br></br>
                            <h3>Descrição</h3>
                            <p className="card__description">{book.textarea}</p><br></br>
                        </div>
                    ))
                }
        </div>    
    )
}
